(ns adventofcode1.two-test
  (:require
   [clojure.spec.test :as stest]
   [clojure.test :refer :all]

   spyscope.core
   [adventofcode1.two :refer :all]
   )
  )

(deftest row-test
  (is (= :1 (row :5 "ULL")))
  (is (= :9 (row :1 "RRDDD")))
  (is (= :8 (row :9 "LURDL")))
  (is (= :5 (row :8 "UUUUD")))
  )

(def sample-data
  [
   "ULL"
   "RRDDD"
   "LURDL"
   "UUUUD"
   ])

(deftest rows-test
  (is (= [:1 :9 :8 :5] (rows sample-data))))


(def puzzle-2a
  [
   ""
   ""
   ""
   ""
   ""
   ])


(deftest puzzle-2a-test
  (is (= [:4 :7 :9 :7 :8] (rows puzzle-2a)))
  )
